(()=>{class t{constructor(){this.tabs=document.querySelectorAll("[data-tab-title]"),this.tabsContent=document.querySelectorAll("[data-tab-content]"),this.init()}init(){this.tabs.forEach(a=>{a.addEventListener("click",()=>{this.reset();var t=a.dataset.tabTitle;a.classList.add("active"),this.showTab(t)})})}showTab(a){this.tabsContent.forEach(t=>{t.dataset.tabContent===a&&t.classList.add("active")})}reset(){this.tabs.forEach(t=>{t.classList.remove("active")}),this.tabsContent.forEach(t=>{t.classList.remove("active")})}}document.addEventListener("DOMContentLoaded",()=>{new t})})();